SELECT base.id cover_id,base.address,base.des,base.district,base.img,base.latitude,base.longitude, base.price,base.project,base.project_district,base.status,base.title,base.transmit_c,base.view_c, d.mark,t.type tag_type,t.id tag_id, t.name tag_name FROM (SELECT info.* FROM (SELECT c.* FROM tag t INNER JOIN cover_tags ct ON ct.tag_id = t.id INNER JOIN cover c ON c.id = ct.cover_id WHERE t.name IN
#{item}
ORDER BY c.id) info GROUP BY info.id HAVING COUNT(info.id) = #{count} LIMIT #{page} , #{size}) AS base LEFT JOIN cover_tags ct ON ct.cover_id = base.id LEFT JOIN tag t ON t.id = ct.tag_id LEFT JOIN devision d on d.cover_id = base.id where base.district = #{district} and base.price between #{minprice} and #{maxprice} and base.title like concat('%',#{title},'%');
